    response: pm.response.json()
});

// get the variable from the postman response
var variable = pm.response.json();

// get the frequencies formatted for amcharts
var frequencies = [];
if (variable.frequencies !== undefined) {
  var frequencySet = variable.frequencies.sets[0];
  for (const [key, value] of Object.entries(frequencySet.map)) {
    var entry = {
      key: key,
      value: value
    };
    frequencies.push(entry);
  }
}

// get the populate vs missing values formatted for am charts
var populated = [];
if (variable.summaryStatistics !== undefined) {
  var statisticSet = variable.summaryStatistics.sets[0];
  // populated entry
  populated.push({
    key: "Populated",
    value: statisticSet.populated
  });
  // missing entry
  populated.push({
    key: "Missing",
    value: statisticSet.missing
  });
}


