    <script>
      pm.getData( function (error, data) {
      // Themes begin
      am4core.useTheme(am4themes_animated);
      // Themes end

      am4core.ready(function () {
        // Create Populated / Missing Chart
        var popChart = am4core.create("populatedChart", am4charts.XYChart);

        // Add data
        popChart.data = data.populated;

        // Create axes
        var populatedCategoryAxis = popChart.yAxes.push(new am4charts.CategoryAxis());
        populatedCategoryAxis.dataFields.category = "key";
        populatedCategoryAxis.renderer.grid.template.location = 0;
        populatedCategoryAxis.renderer.minGridDistance = 30;
        var populatedValueAxis = popChart.xAxes.push(new am4charts.ValueAxis());

        // Create series
        var populatedSeries = popChart.series.push(new am4charts.ColumnSeries());
        populatedSeries.columns.template.width = am4core.percent(20);
        populatedSeries.dataFields.valueX = "value";
        populatedSeries.dataFields.categoryY = "key";
        populatedSeries.name = "Populated and Missing";
        populatedSeries.columns.template.tooltipText = "{categoryY}: [bold]{valueX}[/]";
        populatedSeries.columns.template.fillOpacity = 0.8;

        // Create Frequency Chart
        var chart = am4core.create("frequencyChart", am4charts.XYChart);

        // Add data
        chart.data = data.frequencies;

        // Create axes
        var categoryAxis = chart.yAxes.push(new am4charts.CategoryAxis());
        categoryAxis.dataFields.category = "key";
        categoryAxis.renderer.grid.template.location = 0;
        categoryAxis.renderer.minGridDistance = 30;
        var valueAxis = chart.xAxes.push(new am4charts.ValueAxis());

        // Create series
        var series = chart.series.push(new am4charts.ColumnSeries());
        series.dataFields.valueX = "value";
        series.dataFields.categoryY = "key";
        series.name = "Value Frequencies";
        series.columns.template.tooltipText = "{categoryY}: [bold]{valueX}[/]";
        series.columns.template.fillOpacity = 0.8;

      });
      });
