// get the data set from the postman response
var dataSet = pm.response.json();

// get the variable ids
var variableNames = [];
var variables = {};
for(i = 0; i < dataSet.recordLayout.variables.length; i++){
  var variable = dataSet.recordLayout.variables[i];
  variableNames.push(variable.name)
  variables[variable.id] = variable;
}

// build up some records based on the data and metadata
var nameRecords = [];
var codeRecords = [];
var bothRecords = [];
for (i = 0; i < dataSet.records.length; i++) {
  var record = dataSet.records[i];
  var nameRecord = [];
  var codeRecord = [];
  var bothRecord = [];
  for (d = 0; d < record.data.length; d++) {
    var data = record.data[d];
    var value = data.value.value;
    var nameValue = value;
    var codeValue = value;
    var bothValue = value;
    if (value !== undefined && value.codeValue !== undefined) {
      nameValue = value.name;
      codeValue = value.codeValue;
      bothValue = value.codeValue + ": " + value.name;
    }
    nameRecord.push(nameValue);
    codeRecord.push(codeValue);
    bothRecord.push(bothValue);
  }
  nameRecords.push(nameRecord);
  codeRecords.push(codeRecord);
  bothRecords.push(bothRecord);
}


