              newTh.innerHTML=data.variableNames[i];
              newTh.setAttribute("onclick",  "updateVariable('"+data.variableNames[i]+"')")
              headerRow.appendChild(newTh)
          }

          var tableBody = table.getElementsByTagName('tbody')[0]
          for(i = 0; i < data.nameRecords.length; i++){
