        pm.getData( function (error, data) {
          console.log(id)
          var variable = data.variables[id];
          updateVariableTable(variable);
        });
      }

      function updateVariableTable(variable){
          var variableLabel = document.getElementById("variable-label");
          variableLabel.innerHTML = variable.label;

          var variableDescription = document.getElementById("variable-description");
          variableDescription.innerHTML = variable.description;

          var variableType = document.getElementById("variable-type");
          variableType.innerHTML = variable.dataType;


          createVariableRow(varTableBody, "Label", variable.label);
          createVariableRow(varTableBody, "Description", variable.description);
          createVariableRow(varTableBody, "Question Text", variable.questionText);
          createVariableRow(varTableBody, "Data Type", variable.dataType);
      }

      function createVariableRow(tableBody, name, value){
        if(value !== undefined){
            var row = tableBody.insertRow();
            var nameTd = document.createElement('td');
            nameTd.innerHTML=name;
            var valueTd = document.createElement('td');
            valueTd.innerHTML=value;
            row.appendChild(nameTd)
            row.appendChild(valueTd)
          }
      }

      function updateCodes(id) {
        pm.getData( function (error, data) {
          // refreshes the table 
          var table = document.getElementById("data");
          while (table.rows.length > 1) {
            table.deleteRow(1);
          }

          var records;
          if(id === 'name'){
            records = data.nameRecords;
          }else if(id === 'value'){
            records = data.codeRecords;
          }else{
            records = data.bothRecords;
          }

          var tableBody = table.getElementsByTagName('tbody')[0]
          for(i = 0; i < records.length; i++){
            var record = records[i];
            var newRow = tableBody.insertRow();
            for(d = 0; d < record.length; d++){
